        filename: `${reportName || 'report'}.pdf`,
        filePath: `/exports/${req.params.projectId}/${Date.now()}.pdf`,
        checksum: 'pending', // Will be updated after file generation
        metadata: {
          includeExecutiveSummary: !!executiveSummary,
          exportTimestamp: new Date().toISOString()
        }
      };

      // Validate request data
      const result = insertReportExportSchema.safeParse(exportData);
      if (!result.success) {
        const validationError = fromZodError(result.error);
        return res.status(400).json({ 
          message: "Invalid export data",
          errors: validationError.message 
        });
      }

      // Generate PDF immediately
      const pdfBuffer = await generateReportPDF(req.params.projectId, templateId, {
        reportName,
        reportScope,
        executiveSummary
      }, userId);

      // Set headers for file download
      const filename = `${reportName || 'report'}.pdf`;
      res.setHeader('Content-Type', 'application/pdf');
      res.setHeader('Content-Disposition', `attachment; filename="${filename}"`);
      res.setHeader('Content-Length', pdfBuffer.length);

      // Send the PDF buffer
      res.send(pdfBuffer);
    } catch (error) {
