    label: "User Interaction",
    options: {
      N: { label: "None", value: 0.85 },
      R: { label: "Required", value: 0.62 },
    },
  },
  S: {
    label: "Scope",
    options: {
      U: { label: "Unchanged", value: 1.0 },
      C: { label: "Changed", value: 1.0 },
    },
  },
  C: {
    label: "Confidentiality",
    options: {
      H: { label: "High", value: 0.56 },
      L: { label: "Low", value: 0.22 },
      N: { label: "None", value: 0.0 },
    },
  },
  I: {
    label: "Integrity",
    options: {
      H: { label: "High", value: 0.56 },
      L: { label: "Low", value: 0.22 },
      N: { label: "None", value: 0.0 },
    },
  },
  A: {
    label: "Availability",
    options: {
      H: { label: "High", value: 0.56 },
      L: { label: "Low", value: 0.22 },
      N: { label: "None", value: 0.0 },
    },
  },
};

function calculateCVSS(vector: Record<string, string>) {
  // Get base metric values
  const av = cvssMetrics.AV.options[vector.AV as keyof typeof cvssMetrics.AV.options]?.value || 0;
  const ac = cvssMetrics.AC.options[vector.AC as keyof typeof cvssMetrics.AC.options]?.value || 0;
  const ui = cvssMetrics.UI.options[vector.UI as keyof typeof cvssMetrics.UI.options]?.value || 0;

  // Privileges Required value depends on Scope
  let pr;
  if (vector.S === 'C') { // Scope Changed
    switch (vector.PR) {
      case 'N': pr = 0.85; break;
      case 'L': pr = 0.68; break;
      case 'H': pr = 0.50; break;
      default: pr = 0;
    }
  } else { // Scope Unchanged
    switch (vector.PR) {
      case 'N': pr = 0.85; break;
      case 'L': pr = 0.62; break;
      case 'H': pr = 0.27; break;
      default: pr = 0;
    }
  }

  // Calculate Exploitability sub-score
  const exploitability = 8.22 * av * ac * pr * ui;

  // Get impact values
  const impactConf = cvssMetrics.C.options[vector.C as keyof typeof cvssMetrics.C.options]?.value || 0;
  const impactInteg = cvssMetrics.I.options[vector.I as keyof typeof cvssMetrics.I.options]?.value || 0;
  const impactAvail = cvssMetrics.A.options[vector.A as keyof typeof cvssMetrics.A.options]?.value || 0;

  // Calculate ISC_Base
  const iscBase = 1 - ((1 - impactConf) * (1 - impactInteg) * (1 - impactAvail));

  // Calculate Impact sub-score based on Scope
  let impact;
  if (vector.S === 'U') { // Scope Unchanged
    impact = 6.42 * iscBase;
  } else { // Scope Changed
    impact = 7.52 * (iscBase - 0.029) - 3.25 * Math.pow(iscBase - 0.02, 15);
  }

  // Calculate Base Score
  let baseScore;
  if (impact <= 0) {
    baseScore = 0;
  } else if (vector.S === 'U') {
    baseScore = Math.min(impact + exploitability, 10);
  } else {
    baseScore = Math.min(1.08 * (impact + exploitability), 10);
  }

  // Roundup function - smallest number to 1 decimal place equal or higher than input
  return Math.ceil(baseScore * 10) / 10;
}

function getSeverity(score: number): "critical" | "high" | "medium" | "low" | "informational" {
  if (score >= 9.0) return "critical";
  if (score >= 7.0) return "high";
  if (score >= 4.0) return "medium";
  if (score >= 0.1) return "low";
  return "informational";
}

function parseVector(vectorString: string) {
  const parts = vectorString.replace('CVSS:3.1/', '').split('/');
  const vector: Record<string, string> = {};

  parts.forEach(part => {
    const [key, value] = part.split(':');
    if (key && value) {
      vector[key] = value;
    }
  });

  return vector;
}

export function CVSSCalculator({ value, onChange, disabled }: CVSSCalculatorProps) {
  const [vector, setVector] = useState(() => {
    if (value) {
      return parseVector(value);
    }
    return {
      AV: 'N',
      AC: 'L', 
      PR: 'N',
      UI: 'N',
      S: 'U',
      C: 'N',
      I: 'N',
      A: 'N'
    };
  });

  const score = calculateCVSS(vector);
  const severity = getSeverity(score);
  const vectorString = `CVSS:3.1/AV:${vector.AV}/AC:${vector.AC}/PR:${vector.PR}/UI:${vector.UI}/S:${vector.S}/C:${vector.C}/I:${vector.I}/A:${vector.A}`;

  const handleChange = (metric: string, value: string) => {
    const newVector = { ...vector, [metric]: value };
    setVector(newVector);

    const newScore = calculateCVSS(newVector);
    const newSeverity = getSeverity(newScore);
    const newVectorString = `CVSS:3.1/AV:${newVector.AV}/AC:${newVector.AC}/PR:${newVector.PR}/UI:${newVector.UI}/S:${newVector.S}/C:${newVector.C}/I:${newVector.I}/A:${newVector.A}`;

    onChange?.(newVectorString, newScore, newSeverity);
  };

  return (
    <Card data-testid="cvss-calculator">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span>Base Score</span>
          <div className="flex items-center gap-2">
            <Badge variant="outline" className="font-mono text-sm" data-testid="cvss-score">
              {score.toFixed(1)}
            </Badge>
            <SeverityBadge severity={severity} />
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {Object.entries(cvssMetrics).map(([key, metric]) => (
            <div key={key} className="space-y-3">
              <Label className="text-sm font-medium">{metric.label} ({key})</Label>
              <div className="flex flex-wrap gap-2">
                {Object.entries(metric.options).map(([optionKey, option]) => (
                  <Button
                    key={optionKey}
                    variant={vector[key] === optionKey ? "default" : "outline"}
                    size="sm"
                    onClick={() => handleChange(key, optionKey)}
                    disabled={disabled}
                    data-testid={`button-cvss-${key.toLowerCase()}-${optionKey.toLowerCase()}`}
                    className={cn(
                      "text-xs px-3 py-1 h-auto",
                      vector[key] === optionKey && "bg-primary text-primary-foreground"
                    )}
                  >
                    {option.label} ({optionKey})
                  </Button>
                ))}
              </div>
            </div>
          ))}
        </div>

        <div className="pt-4 border-t">
          <Label>CVSS Vector</Label>
          <div className="mt-1 p-2 bg-muted rounded-md font-mono text-sm break-all" data-testid="cvss-vector">
            {vectorString}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

