import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { SeverityBadge } from './SeverityBadge';
import { StatusBadge } from './StatusBadge';
import {
  Eye,
  MessageSquare,
  Paperclip,
  Calendar,
  MoreHorizontal,
} from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { cn } from '@/lib/utils';

interface FindingCardProps {
  finding: {
    id: string;
    title: string;
    severity: 'critical' | 'high' | 'medium' | 'low' | 'informational';
    status: string;
    description?: string;
    cvssScore?: number;
    createdBy: {
      id: string;
      name: string;
      avatar?: string;
    };
    createdAt: Date;
    updatedAt: Date;
    commentsCount?: number;
    evidenceCount?: number;
    affectedAssets?: string[];
  };
  onView?: (id: string) => void;
  onEdit?: (id: string) => void;
  onStatusChange?: (id: string, status: string) => void;
  className?: string;
}

export function FindingCard({
  finding,
  onView,
  onEdit,
  onStatusChange,
  className,
}: FindingCardProps) {
  const handleView = () => {
    onView?.(finding.id);
    console.log('View finding:', finding.id);
  };

  const handleEdit = () => {
    onEdit?.(finding.id);
    console.log('Edit finding:', finding.id);
  };

  const handleStatusChange = (status: string) => {
    onStatusChange?.(finding.id, status);
    console.log('Change status:', finding.id, status);
  };

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map((n) => n[0])
      .join('')
      .toUpperCase();
  };

  return (
    <Card 
      className={cn('hover-elevate transition-colors', className)} 
      data-testid={`card-finding-${finding.id}`}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between gap-3">
          <div className="flex-1 min-w-0">
            <h3 className="font-semibold text-base leading-tight truncate mb-2">
              {finding.title}
            </h3>
            <div className="flex items-center gap-2 mb-2">
              <SeverityBadge severity={finding.severity} />
              <StatusBadge status={finding.status as any} />
              {finding.cvssScore && (
                <Badge variant="outline" className="font-mono text-xs">
                  {finding.cvssScore.toFixed(1)}
                </Badge>
              )}
            </div>
          </div>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" data-testid={`button-menu-${finding.id}`}>
                <MoreHorizontal className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={handleView}>
                <Eye className="h-4 w-4 mr-2" />
                View Details
              </DropdownMenuItem>
              <DropdownMenuItem onClick={handleEdit}>
                Edit Finding
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => handleStatusChange('submitted')}>
                Mark as Submitted
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => handleStatusChange('closed')}>
                Close Finding
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </CardHeader>

      <CardContent className="pt-0">
        {finding.description && (
          <p className="text-sm text-muted-foreground mb-3 line-clamp-2">
            {finding.description.replace(/<[^>]*>/g, '').substring(0, 120)}...
          </p>
        )}

        {finding.affectedAssets && finding.affectedAssets.length > 0 && (
          <div className="mb-3">
            <div className="flex flex-wrap gap-1">
              {finding.affectedAssets.slice(0, 3).map((asset, index) => (
                <Badge key={index} variant="secondary" className="text-xs">
                  {asset}
                </Badge>
              ))}
              {finding.affectedAssets.length > 3 && (
                <Badge variant="secondary" className="text-xs">
                  +{finding.affectedAssets.length - 3} more
                </Badge>
              )}
            </div>
          </div>
        )}

        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Avatar className="h-6 w-6">
              <AvatarImage src={finding.createdBy.avatar} />
              <AvatarFallback className="text-xs">
                {getInitials(finding.createdBy.name)}
              </AvatarFallback>
            </Avatar>
            <span className="text-xs text-muted-foreground">
              {finding.createdBy.name}
            </span>
          </div>

          <div className="flex items-center gap-3 text-xs text-muted-foreground">
            {finding.commentsCount !== undefined && (
              <div className="flex items-center gap-1">
                <MessageSquare className="h-3 w-3" />
                <span>{finding.commentsCount}</span>
              </div>
            )}
            {finding.evidenceCount !== undefined && (
              <div className="flex items-center gap-1">
                <Paperclip className="h-3 w-3" />
                <span>{finding.evidenceCount}</span>
              </div>
            )}
            <div className="flex items-center gap-1">
              <Calendar className="h-3 w-3" />
              <span>{formatDistanceToNow(finding.updatedAt, { addSuffix: true })}</span>
            </div>
          </div>
        </div>

        <div className="mt-3 flex gap-2">
          <Button 
            size="sm" 
            variant="outline" 
            onClick={handleView}
            data-testid={`button-view-${finding.id}`}
          >
            <Eye className="h-4 w-4 mr-1" />
            View
          </Button>
          <Button 
            size="sm" 
            onClick={handleEdit}
            data-testid={`button-edit-${finding.id}`}
          >
            Edit
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

server/storage.ts.content:
import {
  users,
  organizations,
  projects,
  findings,
  projectMembers,
  evidenceAttachments,
  comments,
  reportTemplates,
  reportExports,
  activityLogs,
  projectCredentials,
  postmanCollections,
  roles,
  rolePermissions,
  userRoles,
  userInvitations,
  type User,
  type UpsertUser,
  type Organization,
  type Project,
  type Finding,
  type ProjectMember,
  type EvidenceAttachment,
  type Comment,
  type ReportTemplate,
  type ReportExport,
  type ActivityLog,
  type ProjectCredential,
  type PostmanCollection,
  type Role,
  type RolePermission,
  type UserRole,
  type UserInvitation,
  type InsertOrganization,
  type InsertProject,
  type InsertFinding,
  type InsertEvidence,
  type InsertComment,
  type InsertTemplate,
  type InsertCredential,
  type InsertPostmanCollection,
  type InsertReportExport,
  type InsertRole,
  type InsertRolePermission,
  type InsertUserRole,
  type InsertUserInvitation,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc } from "drizzle-orm";

// Interface for storage operations - following Replit Auth requirements
export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  deactivateUser(id: string): Promise<User>;
  resetUserPassword(id: string, newPasswordHash: string): Promise<User>;

  // Organization operations
  createOrganization(org: InsertOrganization): Promise<Organization>;
  getOrganization(id: string): Promise<Organization | undefined>;

  // Project operations
  createProject(project: InsertProject): Promise<Project>;
  getProject(id: string): Promise<Project | undefined>;
  getProjectsByOrganization(orgId: string): Promise<Project[]>;
  getProjectsByUser(userId: string): Promise<Project[]>;
  addProjectMember(projectId: string, userId: string, canEdit?: boolean): Promise<ProjectMember>;

  // Project credential operations
  createCredential(credential: InsertCredential): Promise<ProjectCredential>;
  getCredentialsByProject(projectId: string): Promise<ProjectCredential[]>;

  // Postman collection operations
  createPostmanCollection(collection: InsertPostmanCollection): Promise<PostmanCollection>;
  getPostmanCollectionsByProject(projectId: string): Promise<PostmanCollection[]>;

  // Report export operations
  createReportExport(reportExport: InsertReportExport): Promise<ReportExport>;
  getReportExportsByProject(projectId: string): Promise<ReportExport[]>;

  // Finding operations
  createFinding(finding: InsertFinding): Promise<Finding>;
  getFinding(id: string): Promise<Finding | undefined>;
  getFindingsByProject(projectId: string): Promise<Finding[]>;
  getFindingsByUser(userId: string): Promise<(Finding & { project: { name: string; id: string }; reporter: { firstName: string; lastName: string } })[]>;
  updateFinding(id: string, updates: Partial<InsertFinding>): Promise<Finding>;

  // Activity log operations
  createActivityLog(log: any): Promise<ActivityLog>;
  getActivityLogsByFinding(findingId: string): Promise<ActivityLog[]>;

  // Comment operations
  createComment(comment: InsertComment): Promise<Comment>;
  getCommentsByFinding(findingId: string): Promise<(Comment & { author: { firstName: string; lastName: string } })[]>;

  // Evidence operations
  addEvidence(evidence: InsertEvidence): Promise<EvidenceAttachment>;
  getEvidenceByFinding(findingId: string): Promise<EvidenceAttachment[]>;

  // Template operations
  createTemplate(template: InsertTemplate): Promise<ReportTemplate>;
  getTemplate(id: string): Promise<ReportTemplate | undefined>;
  getTemplatesByOrganization(orgId: string): Promise<ReportTemplate[]>;
  updateTemplate(id: string, updates: Partial<InsertTemplate>): Promise<ReportTemplate>;
  deleteTemplate(id: string): Promise<void>;

  // Project membership check
  hasProjectAccess(userId: string, projectId: string): Promise<boolean>;

  // Activity logging
  logActivity(
    actorId: string,
    action: string,
    targetType: string,
    targetId: string,
    oldValues?: any,
    newValues?: any,
    metadata?: any
  ): Promise<ActivityLog>;

  // Role management operations
  createRole(role: InsertRole): Promise<Role>;
  getRole(id: string): Promise<Role | undefined>;
  getRolesByOrganization(orgId: string): Promise<Role[]>;
  updateRole(id: string, updates: Partial<InsertRole>): Promise<Role>;
  deleteRole(id: string): Promise<void>;

  // Role permission operations
  addRolePermission(rolePermission: InsertRolePermission): Promise<RolePermission>;
  removeRolePermission(roleId: string, permission: string): Promise<void>;
  getRolePermissions(roleId: string): Promise<string[]>;

  // User role operations
  assignUserRole(userRole: InsertUserRole): Promise<UserRole>;
  removeUserRole(userId: string, roleId: string): Promise<void>;
  getUserRoles(userId: string): Promise<(UserRole & { role: Role })[]>;
  getUsersByRole(roleId: string): Promise<(UserRole & { user: User })[]>;

  // User invitation operations
  createUserInvitation(invitation: InsertUserInvitation): Promise<UserInvitation>;
  getUserInvitations(organizationId: string): Promise<(UserInvitation & { role: Role; invitedByUser: User })[]>;
  getInvitationByToken(token: string): Promise<UserInvitation | undefined>;
  acceptInvitation(token: string, userId: string): Promise<UserRole>;

  // User management operations
  getUsersByOrganization(organizationId: string): Promise<(User & { roles: Role[] })[]>;
  updateUserRole(userId: string, organizationId: string, newRole: string): Promise<UserRole>;
  removeUserFromOrganization(userId: string, organizationId: string): Promise<void>;

  // Performance optimization - get all user permissions in one query
  getUserPermissions(userId: string): Promise<Set<string>>;
}

export class DatabaseStorage implements IStorage {
  // User operations (mandatory for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async deactivateUser(id: string): Promise<User> {
    const [deactivatedUser] = await db
      .update(users)
      .set({
        isActive: false,
        updatedAt: new Date(),
      })
      .where(eq(users.id, id))
      .returning();
    return deactivatedUser;
  }

  async resetUserPassword(id: string, newPasswordHash: string): Promise<User> {
    const [updatedUser] = await db
      .update(users)
      .set({
        passwordHash: newPasswordHash,
        updatedAt: new Date(),
      })
      .where(eq(users.id, id))
      .returning();
    return updatedUser;
  }

  // Organization operations
  async createOrganization(orgData: InsertOrganization): Promise<Organization> {
    const [org] = await db.insert(organizations).values(orgData).returning();
    return org;
  }

  async getOrganization(id: string): Promise<Organization | undefined> {
    const [org] = await db.select().from(organizations).where(eq(organizations.id, id));
    return org;
  }

  // Project operations
  async createProject(projectData: InsertProject): Promise<Project> {
    const [project] = await db.insert(projects).values(projectData).returning();
    return project;
  }

  async getProject(id: string): Promise<Project | undefined> {
    const [project] = await db.select().from(projects).where(eq(projects.id, id));
    return project;
  }

  async getProjectsByOrganization(orgId: string): Promise<Project[]> {
    return await db.select().from(projects).where(eq(projects.organizationId, orgId));
  }

  async getProjectsByUser(userId: string): Promise<Project[]> {
    const results = await db
      .select()
      .from(projects)
      .innerJoin(projectMembers, eq(projects.id, projectMembers.projectId))
      .where(eq(projectMembers.userId, userId));

    return results.map(r => r.projects);
  }

  async addProjectMember(projectId: string, userId: string, canEdit = false): Promise<ProjectMember> {
    const [member] = await db
      .insert(projectMembers)
      .values({ projectId, userId, canEdit })
      .returning();
    return member;
  }

