client/src/components/FileUpload.tsx.content:
import { useState, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Upload, X, Copy, File, Image as ImageIcon } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";

interface UploadedFile {
  id: string;
  name: string;
  size: number;
  type: string;
  url: string;
  markdownSyntax: string;
}

interface FileUploadProps {
  onFilesChange?: (files: UploadedFile[]) => void;
  accept?: string;
  multiple?: boolean;
  maxSize?: number; // in MB
  className?: string;
}

export function FileUpload({
  onFilesChange,
  accept = ".png,.jpg,.jpeg,.svg",
  multiple = true,
  maxSize = 10,
  className
}: FileUploadProps) {
  const [files, setFiles] = useState<UploadedFile[]>([]);
  const [isDragging, setIsDragging] = useState(false);
  const [uploading, setUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const supportedTypes = ['image/png', 'image/jpeg', 'image/jpg', 'image/svg+xml'];

  const handleFileSelect = async (selectedFiles: FileList | null) => {
    if (!selectedFiles) return;

    const validFiles: File[] = [];

    for (let i = 0; i < selectedFiles.length; i++) {
      const file = selectedFiles[i];

      // Check file type
      if (!supportedTypes.includes(file.type)) {
        toast({
          title: "Invalid file type",
          description: `${file.name} is not a supported image format. Please use PNG, JPEG, or SVG.`,
          variant: "destructive",
        });
        continue;
      }

      // Check file size
      if (file.size > maxSize * 1024 * 1024) {
        toast({
          title: "File too large",
          description: `${file.name} is larger than ${maxSize}MB. Please choose a smaller file.`,
          variant: "destructive",
        });
        continue;
      }

      validFiles.push(file);
    }

    if (validFiles.length === 0) return;

    setUploading(true);

    try {
      const uploadedFiles: UploadedFile[] = [];

      for (const file of validFiles) {
        // For now, create a local URL - in a real app this would upload to a server
        const url = URL.createObjectURL(file);
        const markdownSyntax = `![${file.name}](${url})`;

        const uploadedFile: UploadedFile = {
          id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          name: file.name,
          size: file.size,
          type: file.type,
          url,
          markdownSyntax
        };

        uploadedFiles.push(uploadedFile);
      }

      const newFiles = [...files, ...uploadedFiles];
      setFiles(newFiles);
      onFilesChange?.(newFiles);

      toast({
        title: "Files uploaded",
        description: `${validFiles.length} file(s) uploaded successfully.`,
      });
    } catch (error) {
      toast({
        title: "Upload failed",
        description: "Failed to upload files. Please try again.",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    handleFileSelect(e.dataTransfer.files);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const removeFile = (fileId: string) => {
    const newFiles = files.filter(f => f.id !== fileId);
    setFiles(newFiles);
    onFilesChange?.(newFiles);
  };

  const copyMarkdown = (markdownSyntax: string) => {
    navigator.clipboard.writeText(markdownSyntax);
    toast({
      title: "Copied to clipboard",
      description: "Markdown syntax copied. You can paste it in any text field.",
    });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getFileIcon = (type: string) => {
    if (type.startsWith('image/')) {
      return <ImageIcon className="h-4 w-4" />;
    }
    return <File className="h-4 w-4" />;
  };

  return (
    <Card className={cn("w-full", className)} data-testid="file-upload">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Upload className="h-5 w-5" />
          Proof of Concept Files
        </CardTitle>
        <p className="text-sm text-muted-foreground">
          Upload screenshots and images to support your findings. Supported formats: PNG, JPEG, SVG (max {maxSize}MB each)
        </p>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Upload Area */}
        <div
          className={cn(
            "border-2 border-dashed rounded-lg p-6 text-center transition-colors cursor-pointer",
            isDragging
              ? "border-primary bg-primary/5"
              : "border-muted-foreground/25 hover:border-primary hover:bg-primary/5",
            uploading && "opacity-50 pointer-events-none"
          )}
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onClick={() => fileInputRef.current?.click()}
          data-testid="upload-area"
        >
          <Upload className="h-8 w-8 mx-auto mb-2 text-muted-foreground" />
          <p className="text-sm font-medium mb-1">
            {uploading ? "Uploading..." : "Drop files here or click to upload"}
          </p>
          <p className="text-xs text-muted-foreground">
            PNG, JPEG, SVG up to {maxSize}MB each
          </p>
        </div>

        <Input
          ref={fileInputRef}
          type="file"
          accept={accept}
          multiple={multiple}
          onChange={(e) => handleFileSelect(e.target.files)}
          className="hidden"
          data-testid="file-input"
        />

        {/* Uploaded Files List */}
        {files.length > 0 && (
          <>
            <Separator />
            <div className="space-y-3">
              <Label className="text-sm font-medium">Uploaded Files</Label>
              {files.map((file) => (
                <div
                  key={file.id}
                  className="flex items-center gap-3 p-3 bg-muted/30 rounded-lg"
                  data-testid={`uploaded-file-${file.id}`}
                >
                  {getFileIcon(file.type)}
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium truncate">{file.name}</p>
                    <div className="flex items-center gap-2 text-xs text-muted-foreground">
                      <span>{formatFileSize(file.size)}</span>
                      <Badge variant="outline" className="text-xs">
                        {file.type.split('/')[1].toUpperCase()}
                      </Badge>
                    </div>
                  </div>
                  <div className="flex items-center gap-1">
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      onClick={() => copyMarkdown(file.markdownSyntax)}
                      data-testid={`copy-markdown-${file.id}`}
                    >
                      <Copy className="h-4 w-4" />
                    </Button>
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      onClick={() => removeFile(file.id)}
                      data-testid={`remove-file-${file.id}`}
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {/* Usage Instructions */}
        {files.length > 0 && (
          <>
            <Separator />
            <div className="text-xs text-muted-foreground space-y-1">
              <p><strong>Usage:</strong></p>
              <p> Click the copy button to get markdown syntax for any uploaded image</p>
              <p> Paste the markdown syntax in description, steps, impact, or fix fields to display images inline</p>
              <p> Images without markdown syntax will be displayed in the "Proof of Concept" section</p>
            </div>
          </>
        )}
      </CardContent>
    </Card>
  );
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

// Role-based access control middleware
export const hasPermission = (requiredPermission: string): RequestHandler => {
  return async (req, res, next) => {
    try {
      const user = req.user as any;

      if (!req.isAuthenticated() || !user?.claims?.sub) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const userId = user.claims.sub;

      // Check if user is super admin first (bypass all permissions)
      const dbUser = await storage.getUser(userId);
      if (dbUser?.role === 'super_admin') {
        return next();
      }

      // Get user permissions efficiently in one query
      const userPermissions = await storage.getUserPermissions(userId);

      if (!userPermissions.has(requiredPermission)) {
        return res.status(403).json({ 
          message: "Access denied", 
          requiredPermission 
        });
      }

      next();
    } catch (error) {
      console.error("Error checking permissions:", error);
      res.status(500).json({ message: "Error checking permissions" });
    }
  };
};

// Check multiple permissions (user must have ALL of them)
export const hasAllPermissions = (requiredPermissions: string[]): RequestHandler => {
  return async (req, res, next) => {
    try {
      const user = req.user as any;

      if (!req.isAuthenticated() || !user?.claims?.sub) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const userId = user.claims.sub;

      // Check if user is super admin first (bypass all permissions)
      const dbUser = await storage.getUser(userId);
      if (dbUser?.role === 'super_admin') {
        return next();
      }

      // Get user permissions efficiently in one query
      const userPermissions = await storage.getUserPermissions(userId);

      // Check if user has all required permissions
      const missingPermissions = requiredPermissions.filter(p => !userPermissions.has(p));

      if (missingPermissions.length > 0) {
        return res.status(403).json({ 
          message: "Access denied", 
          missingPermissions 
        });
      }

      next();
    } catch (error) {
      console.error("Error checking permissions:", error);
      res.status(500).json({ message: "Error checking permissions" });
    }
  };
};

// Check if user has any of the provided permissions
export const hasAnyPermission = (allowedPermissions: string[]): RequestHandler => {
  return async (req, res, next) => {
    try {
      const user = req.user as any;

      if (!req.isAuthenticated() || !user?.claims?.sub) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const userId = user.claims.sub;

      // Check if user is super admin first (bypass all permissions)
      const dbUser = await storage.getUser(userId);
      if (dbUser?.role === 'super_admin') {
        return next();
      }

      // Get user permissions efficiently in one query
      const userPermissions = await storage.getUserPermissions(userId);

      // Check if user has any of the allowed permissions
      const hasAllowedPermission = allowedPermissions.some(p => userPermissions.has(p));

      if (!hasAllowedPermission) {
        return res.status(403).json({ 
          message: "Access denied", 
          allowedPermissions 
        });
      }

      next();
    } catch (error) {
      console.error("Error checking permissions:", error);
      res.status(500).json({ message: "Error checking permissions" });
    }
  };
};

// Super admin check - users with role 'super_admin'
export const isSuperAdmin: RequestHandler = async (req, res, next) => {
  try {
    const user = req.user as any;

    if (!req.isAuthenticated() || !user?.claims?.sub) {
      return res.status(401).json({ message: "Unauthorized" });
    }

    const userId = user.claims.sub;
    const dbUser = await storage.getUser(userId);

    if (dbUser?.role !== 'super_admin') {
      return res.status(403).json({ 
        message: "Super admin access required" 
      });
    }

    next();
  } catch (error) {
    console.error("Error checking super admin status:", error);
    res.status(500).json({ message: "Error checking admin status" });
  }
};

