  --shadow-sm: 0px 2px 0px 0px hsl(215 16% 75% / 0.05), 0px 1px 2px -1px hsl(215 16% 75% / 0.10);
  --shadow: 0px 2px 0px 0px hsl(215 16% 75% / 0.05), 0px 1px 2px -1px hsl(215 16% 75% / 0.10);
