client/src/pages/CreateFinding.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation, Link } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { ArrowLeft, Save } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { CVSSCalculator } from "@/components/CVSSCalculator";
import { RichTextEditor } from "@/components/RichTextEditor";
import { FileUpload } from "@/components/FileUpload";
import { FindingPreview } from "@/components/FindingPreview";

const createFindingSchema = z.object({
  title: z.string().min(1, "Title is required"),
  projectId: z.string().min(1, "Project is required"),
  severity: z.enum(["critical", "high", "medium", "low", "informational"]),
  cvssVector: z.string().optional(),
  cvssScore: z.number().optional(),
  descriptionHtml: z.string().optional(),
  stepsHtml: z.string().optional(),
  impactHtml: z.string().optional(),
  fixHtml: z.string().optional(),
  proofOfConceptFiles: z.array(z.object({
    id: z.string(),
    name: z.string(),
    size: z.number(),
    type: z.string(),
    url: z.string(),
    markdownSyntax: z.string(),
  })).optional(),
});

type CreateFindingForm = z.infer<typeof createFindingSchema>;

interface Project {
  id: string;
  name: string;
}

export default function CreateFinding() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const { data: projects = [], isLoading: loadingProjects } = useQuery({
    queryKey: ['/api/projects'],
  });

  const form = useForm<CreateFindingForm>({
    resolver: zodResolver(createFindingSchema),
    defaultValues: {
      title: "",
      projectId: "",
      severity: "medium",
      cvssVector: "",
      cvssScore: 0,
      descriptionHtml: "",
      stepsHtml: "",
      impactHtml: "",
      fixHtml: "",
      proofOfConceptFiles: [],
    },
  });

  const createMutation = useMutation({
    mutationFn: (data: CreateFindingForm) =>
      fetch(`/api/projects/${data.projectId}/findings`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      }).then(res => {
        if (!res.ok) throw new Error('Failed to create finding');
        return res.json();
      }),
    onSuccess: (finding) => {
      queryClient.invalidateQueries({ queryKey: ['/api/findings'] });
      toast({
        title: "Finding created",
        description: "The finding has been created successfully.",
      });
      setLocation(`/findings/${finding.id}`);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create finding. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: CreateFindingForm) => {
    createMutation.mutate(data);
  };

  const typedProjects = projects as Project[];

  return (
    <div className="p-6" data-testid="page-create-finding">
      <div className="flex flex-col gap-6 max-w-4xl mx-auto">
        {/* Header */}
        <div className="flex items-center gap-4">
          <Link href="/findings">
            <Button variant="ghost" size="sm" data-testid="button-back-to-findings">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Findings
            </Button>
          </Link>
          <div>
            <h1 className="text-2xl font-bold" data-testid="heading-create-finding">
              Create New Finding
            </h1>
            <p className="text-muted-foreground">
              Document a new security finding for your project
            </p>
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            {/* Basic Information */}
            <Card>
              <CardHeader>
                <CardTitle>Basic Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Finding Title *</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            placeholder="Enter finding title..."
                            data-testid="input-title"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="projectId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Project *</FormLabel>
                        <Select
                          onValueChange={field.onChange}
                          value={field.value}
                          disabled={loadingProjects}
                        >
                          <FormControl>
                            <SelectTrigger data-testid="select-project">
                              <SelectValue placeholder="Select project..." />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {typedProjects.map((project) => (
                              <SelectItem key={project.id} value={project.id}>
                                {project.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="col-span-full">
                  <CVSSCalculator
                    value={form.watch('cvssVector')}
                    onChange={(vector, score, severity) => {
                      form.setValue('cvssVector', vector);
                      form.setValue('cvssScore', score);
                      form.setValue('severity', severity as any);
                    }}
                  />
                </div>
              </CardContent>
            </Card>

            {/* Technical Details - One by One Layout */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle>Vulnerability Description</CardTitle>
                </CardHeader>
                <CardContent>
                  <FormField
                    control={form.control}
                    name="descriptionHtml"
                    render={({ field }) => (
                      <FormItem>
                        <FormControl>
                          <RichTextEditor
                            content={field.value || ''}
                            onChange={field.onChange}
                            placeholder="Describe the vulnerability in detail..."
                            className="min-h-[200px]"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Steps to Reproduce</CardTitle>
                </CardHeader>
                <CardContent>
                  <FormField
                    control={form.control}
                    name="stepsHtml"
                    render={({ field }) => (
                      <FormItem>
                        <FormControl>
                          <RichTextEditor
                            content={field.value || ''}
                            onChange={field.onChange}
                            placeholder="1. Step one\n2. Step two\n3. Result"
                            className="min-h-[200px]"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Impact Assessment</CardTitle>
                </CardHeader>
                <CardContent>
                  <FormField
                    control={form.control}
                    name="impactHtml"
                    render={({ field }) => (
                      <FormItem>
                        <FormControl>
                          <RichTextEditor
                            content={field.value || ''}
                            onChange={field.onChange}
                            placeholder="Describe the potential impact and risks..."
                            className="min-h-[200px]"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Fix Recommendations</CardTitle>
                </CardHeader>
                <CardContent>
                  <FormField
                    control={form.control}
                    name="fixHtml"
                    render={({ field }) => (
                      <FormItem>
                        <FormControl>
                          <RichTextEditor
                            content={field.value || ''}
                            onChange={field.onChange}
                            placeholder="Provide recommendations for fixing this vulnerability..."
                            className="min-h-[200px]"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </CardContent>
              </Card>
            </div>

            {/* Proof of Concept Files - Optional */}
            <Card>
              <CardHeader>
                <CardTitle>Proof of Concept Files (Optional)</CardTitle>
                <p className="text-sm text-muted-foreground">
                  Upload screenshots, files, or other evidence to support your finding
                </p>
              </CardHeader>
              <CardContent>
                <FileUpload
                  onFilesChange={(files) => {
                    form.setValue('proofOfConceptFiles', files);
                  }}
                  multiple={true}
                  maxSize={10}
                />
              </CardContent>
            </Card>

            {/* Action Buttons */}
            <div className="flex items-center justify-between">
              <FindingPreview
                title={form.watch('title')}
                severity={form.watch('severity')}
                cvssVector={form.watch('cvssVector')}
                cvssScore={form.watch('cvssScore')}
                descriptionHtml={form.watch('descriptionHtml')}
                stepsHtml={form.watch('stepsHtml')}
                impactHtml={form.watch('impactHtml')}
                fixHtml={form.watch('fixHtml')}
                proofOfConceptFiles={form.watch('proofOfConceptFiles')}
                projectName={typedProjects.find(p => p.id === form.watch('projectId'))?.name}
              />
              <div className="flex items-center gap-4">
                <Link href="/findings">
                  <Button
                    type="button"
                    variant="outline"
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                </Link>
                <Button
                  type="submit"
                  disabled={createMutation.isPending}
                  data-testid="button-create-finding"
                >
                  <Save className="h-4 w-4 mr-2" />
                  {createMutation.isPending ? "Creating..." : "Create Finding"}
                </Button>
              </div>
            </div>
          </form>
        </Form>
      </div>
    </div>
  );
}

client/src/components/FindingPreview.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Eye, Calendar, User, FileText } from "lucide-react";
import { SeverityBadge } from "@/components/SeverityBadge";
import { format } from "date-fns";

interface FindingPreviewProps {
  title: string;
  severity: string;
  cvssVector?: string;
  cvssScore?: number;
  descriptionHtml?: string;
  stepsHtml?: string;
  impactHtml?: string;
  fixHtml?: string;
  proofOfConceptFiles?: Array<{
    id: string;
    name: string;
    type: string;
    url: string;
  }>;
  projectName?: string;
  trigger?: React.ReactNode;
}

export function FindingPreview({
  title,
  severity,
  cvssVector,
  cvssScore,
  descriptionHtml,
  stepsHtml,
  impactHtml,
  fixHtml,
  proofOfConceptFiles = [],
  projectName = "Selected Project",
  trigger
}: FindingPreviewProps) {
  const defaultTrigger = (
    <Button variant="outline" data-testid="button-preview-finding">
      <Eye className="h-4 w-4 mr-2" />
      Preview
    </Button>
  );

  // Helper function to render HTML content safely
  const renderHtmlContent = (html: string | undefined) => {
    if (!html || html.trim() === '') {
      return <p className="text-muted-foreground italic">No content provided</p>;
    }
    return (
      <div 
        className="prose prose-sm dark:prose-invert max-w-none"
        dangerouslySetInnerHTML={{ __html: html }}
      />
    );
  };

  // Helper function to check if markdown image syntax exists in content
  const hasMarkdownImages = (html: string | undefined) => {
    if (!html) return false;
    return /!\[.*?\]\(.*?\)/.test(html);
  };

  // Filter files that are not referenced in markdown
  const getUnreferencedFiles = () => {
    const allMarkdownContent = [descriptionHtml, stepsHtml, impactHtml, fixHtml].join(' ');
    return proofOfConceptFiles.filter(file => 
      !allMarkdownContent.includes(file.url)
    );
  };

  const unreferencedFiles = getUnreferencedFiles();

  return (
    <Dialog>
      <DialogTrigger asChild>
        {trigger || defaultTrigger}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto" data-testid="finding-preview-dialog">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Finding Preview
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Header Section */}
          <div className="space-y-4">
            <div>
              <h1 className="text-2xl font-bold" data-testid="preview-title">
                {title || "Untitled Finding"}
              </h1>
              <div className="flex items-center gap-4 mt-2 text-sm text-muted-foreground">
                <div className="flex items-center gap-1">
                  <User className="h-4 w-4" />
                  <span>Current User</span>
                </div>
                <div className="flex items-center gap-1">
                  <Calendar className="h-4 w-4" />
                  <span>{format(new Date(), 'MMM d, yyyy')}</span>
                </div>
                <Badge variant="outline">{projectName}</Badge>
              </div>
            </div>

            {/* Severity and CVSS */}
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <span className="text-sm font-medium">Severity:</span>
                <SeverityBadge severity={severity as any} />
              </div>
              {cvssScore && cvssScore > 0 && (
                <div className="flex items-center gap-2">
                  <span className="text-sm font-medium">CVSS Score:</span>
                  <Badge variant="outline" className="font-mono">
                    {cvssScore.toFixed(1)}
                  </Badge>
                </div>
              )}
            </div>

            {cvssVector && (
              <div className="p-3 bg-muted rounded-md">
                <div className="text-sm font-medium mb-1">CVSS Vector</div>
                <code className="text-xs text-muted-foreground break-all">
                  {cvssVector}
                </code>
              </div>
            )}
          </div>

          {/* Content Sections */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Description */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Vulnerability Description</CardTitle>
              </CardHeader>
              <CardContent>
                {renderHtmlContent(descriptionHtml)}
              </CardContent>
            </Card>

            {/* Steps to Reproduce */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Steps to Reproduce</CardTitle>
              </CardHeader>
              <CardContent>
                {renderHtmlContent(stepsHtml)}
              </CardContent>
            </Card>

            {/* Impact Assessment */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Impact Assessment</CardTitle>
              </CardHeader>
              <CardContent>
                {renderHtmlContent(impactHtml)}
              </CardContent>
            </Card>

            {/* Fix Recommendations */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Fix Recommendations</CardTitle>
              </CardHeader>
              <CardContent>
                {renderHtmlContent(fixHtml)}
              </CardContent>
            </Card>
          </div>

          {/* Proof of Concept Files */}
          {unreferencedFiles.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Proof of Concept</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {unreferencedFiles.map((file) => (
                    <div
                      key={file.id}
                      className="border rounded-lg p-4 space-y-2"
                      data-testid={`preview-file-${file.id}`}
                    >
                      {file.type.startsWith('image/') ? (
                        <img
                          src={file.url}
                          alt={file.name}
                          className="w-full h-auto rounded-md max-h-64 object-contain"
                        />
                      ) : (
                        <div className="flex items-center justify-center h-32 bg-muted rounded-md">
                          <FileText className="h-8 w-8 text-muted-foreground" />
                        </div>
                      )}
                      <p className="text-sm font-medium truncate">{file.name}</p>
                      <Badge variant="outline" className="text-xs">
                        {file.type.split('/')[1]?.toUpperCase() || 'FILE'}
                      </Badge>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Information Notice */}
          <div className="text-xs text-muted-foreground bg-muted/30 p-3 rounded-md">
            <p><strong>Preview Note:</strong></p>
            <p> This is how your finding will appear once saved</p>
            <p> Images referenced in markdown will display inline within the content sections</p>
            <p> Images not referenced in markdown will appear in the "Proof of Concept" section</p>
            <p> You can still edit the finding after saving</p>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { relations } from "drizzle-orm";
import {
  index,
  jsonb,
  pgEnum,
  pgTable,
  text,
  timestamp,
  varchar,
  real,
  integer,
  boolean,
  uniqueIndex,
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table - mandatory for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// Enums
export const userRoleEnum = pgEnum("user_role", [
  "researcher",
  "project_user",
  "customer_admin",
  "org_admin",
  "super_admin",
]);

export const permissionEnum = pgEnum("permission", [
  "view_assigned_projects",
  "submit_finding",
  "view_finding",
  "edit_finding",
  "edit_comment",
  "view_comment",
  "invite_users",
  "create_projects",
  "edit_projects",
  "export_reports",
  "view_all_projects",
  "manage_users",
  "manage_roles",
]);

export const projectStatusEnum = pgEnum("project_status", [
  "planned",
  "in_progress",
  "complete",
]);

export const findingStatusEnum = pgEnum("finding_status", [
  "draft",
  "submitted",
  "company_review",
  "remediation_in_progress",
  "ready_for_retest",
  "verified_fixed",
  "risk_accepted",
  "closed",
]);

export const severityEnum = pgEnum("severity", [
  "critical",
  "high",
  "medium",
  "low",
  "informational",
]);

export const templateTypeEnum = pgEnum("template_type", [
  "html",
  "docx", 
  "markdown",
]);

export const credentialTypeEnum = pgEnum("credential_type", [
  "user",
  "admin", 
  "service",
]);

export const reportTemplateTypeEnum = pgEnum("report_template_type", [
  "web",
  "mobile",
  "network",
  "cloud",
  "api",
  "executive-summary",
  "html",
]);

// Core entities
export const organizations = pgTable("organizations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  domain: text("domain"),
  settings: jsonb("settings").default({}),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  passwordHash: text("password_hash"),
  organizationId: varchar("organization_id").references(() => organizations.id),
  role: userRoleEnum("role").notNull().default("researcher"),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const projects = pgTable("projects", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  customerName: text("customer_name").notNull(),
  scope: text("scope"),
  methodology: text("methodology"),
  startDate: timestamp("start_date"),
  endDate: timestamp("end_date"),
  status: projectStatusEnum("status").notNull().default("planned"),
  organizationId: varchar("organization_id")
    .references(() => organizations.id)
    .notNull(),
  createdBy: varchar("created_by").references(() => users.id),
  tags: text("tags").array().default([]),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const projectMembers = pgTable("project_members", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id")
    .references(() => projects.id, { onDelete: "cascade" })
    .notNull(),
  userId: varchar("user_id")
    .references(() => users.id, { onDelete: "cascade" })
    .notNull(),
  canEdit: boolean("can_edit").default(false),
  createdAt: timestamp("created_at").defaultNow(),
});

export const findings = pgTable("findings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id")
    .references(() => projects.id, { onDelete: "cascade" })
    .notNull(),
  title: text("title").notNull(),
  descriptionHtml: text("description_html"),
  stepsHtml: text("steps_html"),
  impactHtml: text("impact_html"),
  fixHtml: text("fix_html"),
  references: text("references").array().default([]),
  affectedAssets: text("affected_assets").array().default([]),
  tags: text("tags").array().default([]),
  cvssVector: text("cvss_vector"),
  cvssScore: real("cvss_score"),
  severity: severityEnum("severity"),
  manualSeverityOverride: text("manual_severity_override"),
  status: findingStatusEnum("status").notNull().default("draft"),
  createdBy: varchar("created_by")
    .references(() => users.id)
    .notNull(),
  assignedTo: varchar("assigned_to").references(() => users.id),
  isDuplicate: boolean("is_duplicate").default(false),
  duplicateOf: varchar("duplicate_of"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const evidenceAttachments = pgTable("evidence_attachments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  findingId: varchar("finding_id")
    .references(() => findings.id, { onDelete: "cascade" })
    .notNull(),
  filename: text("filename").notNull(),
  originalFilename: text("original_filename").notNull(),
  mimeType: text("mime_type").notNull(),
  fileSize: integer("file_size").notNull(),
  filePath: text("file_path").notNull(),
  fileHash: text("file_hash").notNull(),
  caption: text("caption"),
  description: text("description"),
  uploadedBy: varchar("uploaded_by")
    .references(() => users.id)
    .notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const comments = pgTable("comments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  findingId: varchar("finding_id")
    .references(() => findings.id, { onDelete: "cascade" })
    .notNull(),
  authorId: varchar("author_id")
    .references(() => users.id)
    .notNull(),
  content: text("content").notNull(),
  isPrivate: boolean("is_private").default(false),
  mentions: text("mentions").array().default([]),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const reportTemplates = pgTable("report_templates", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  type: templateTypeEnum("type").notNull(),
  content: text("content").notNull(),
  variables: jsonb("variables").default([]),
  organizationId: varchar("organization_id").references(() => organizations.id),
  customerId: varchar("customer_id"),
  isDefault: boolean("is_default").default(false),
  version: integer("version").default(1),
  createdBy: varchar("created_by")
    .references(() => users.id)
    .notNull(),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const projectCredentials = pgTable("project_credentials", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id")
    .references(() => projects.id, { onDelete: "cascade" })
    .notNull(),
  name: text("name").notNull(),
  type: credentialTypeEnum("type").notNull(),
  username: text("username"),
  password: text("password"),
  environment: text("environment").notNull(),
  description: text("description"),
  notes: text("notes"),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const postmanCollections = pgTable("postman_collections", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id")
    .references(() => projects.id, { onDelete: "cascade" })
    .notNull(),
  name: text("name").notNull(),
  description: text("description"),
  fileName: text("file_name").notNull(),
  filePath: text("file_path").notNull(),
  fileSize: integer("file_size").notNull(),
  uploadedBy: varchar("uploaded_by").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const reportExports = pgTable("report_exports", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id")
    .references(() => projects.id)
    .notNull(),
  templateId: varchar("template_id")
    .references(() => reportTemplates.id),
  reportName: text("report_name").notNull(),
  reportScope: text("report_scope"),
  templateType: reportTemplateTypeEnum("template_type").notNull(),
  executiveSummary: text("executive_summary"),
  format: text("format").notNull(),
  filename: text("filename").notNull(),
  filePath: text("file_path").notNull(),
  checksum: text("checksum").notNull(),
  metadata: jsonb("metadata").default({}),
  exportedBy: varchar("exported_by")
    .references(() => users.id)
    .notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const activityLogs = pgTable("activity_logs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  actorId: varchar("actor_id")
    .references(() => users.id)
    .notNull(),
  action: text("action").notNull(),
  targetType: text("target_type").notNull(),
  targetId: varchar("target_id").notNull(),
  oldValues: jsonb("old_values"),
  newValues: jsonb("new_values"),
  ipAddress: text("ip_address"),
  userAgent: text("user_agent"),
  metadata: jsonb("metadata").default({}),
  createdAt: timestamp("created_at").defaultNow(),
});

// Role and Permission Management
export const roles = pgTable("roles", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull().unique(),
  description: text("description"),
  organizationId: varchar("organization_id")
    .references(() => organizations.id)
    .notNull(),
  isSystemRole: boolean("is_system_role").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const rolePermissions = pgTable("role_permissions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  roleId: varchar("role_id")
    .references(() => roles.id, { onDelete: "cascade" })
    .notNull(),
  permission: permissionEnum("permission").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const userRoles = pgTable("user_roles", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id")
    .references(() => users.id, { onDelete: "cascade" })
    .notNull(),
  roleId: varchar("role_id")
    .references(() => roles.id, { onDelete: "cascade" })
    .notNull(),
  assignedBy: varchar("assigned_by").references(() => users.id),
  createdAt: timestamp("created_at").defaultNow(),
}, (table) => [
  uniqueIndex("idx_user_roles_unique").on(table.userId, table.roleId),
]);

export const userInvitations = pgTable("user_invitations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").notNull(),
  organizationId: varchar("organization_id")
    .references(() => organizations.id)
    .notNull(),
  roleId: varchar("role_id")
    .references(() => roles.id)
    .notNull(),
  invitedBy: varchar("invited_by")
    .references(() => users.id)
    .notNull(),
  token: varchar("token").notNull().unique(),
  expiresAt: timestamp("expires_at").notNull(),
  acceptedAt: timestamp("accepted_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// Relations
export const organizationsRelations = relations(organizations, ({ many }) => ({
  users: many(users),
  projects: many(projects),
  templates: many(reportTemplates),
  roles: many(roles),
  invitations: many(userInvitations),
}));

export const usersRelations = relations(users, ({ one, many }) => ({
  organization: one(organizations, {
    fields: [users.organizationId],
    references: [organizations.id],
  }),
  createdProjects: many(projects),
  projectMemberships: many(projectMembers),
  findings: many(findings),
  comments: many(comments),
  uploads: many(evidenceAttachments),
  templates: many(reportTemplates),
  exports: many(reportExports),
  activities: many(activityLogs),
  roleAssignments: many(userRoles),
  sentInvitations: many(userInvitations),
}));

export const projectsRelations = relations(projects, ({ one, many }) => ({
  organization: one(organizations, {
    fields: [projects.organizationId],
    references: [organizations.id],
  }),
  createdBy: one(users, {
    fields: [projects.createdBy],
    references: [users.id],
  }),
  members: many(projectMembers),
  findings: many(findings),
  exports: many(reportExports),
}));

export const projectMembersRelations = relations(projectMembers, ({ one }) => ({
  project: one(projects, {
    fields: [projectMembers.projectId],
    references: [projects.id],
  }),
  user: one(users, {
    fields: [projectMembers.userId],
    references: [users.id],
  }),
}));

export const findingsRelations = relations(findings, ({ one, many }) => ({
  project: one(projects, {
    fields: [findings.projectId],
    references: [projects.id],
  }),
  createdBy: one(users, {
    fields: [findings.createdBy],
    references: [users.id],
  }),
  assignedTo: one(users, {
    fields: [findings.assignedTo],
    references: [users.id],
  }),
  duplicateOf: one(findings, {
    fields: [findings.duplicateOf],
    references: [findings.id],
  }),
  evidence: many(evidenceAttachments),
  comments: many(comments),
}));

export const evidenceAttachmentsRelations = relations(
  evidenceAttachments,
  ({ one }) => ({
    finding: one(findings, {
      fields: [evidenceAttachments.findingId],
      references: [findings.id],
    }),
    uploadedBy: one(users, {
      fields: [evidenceAttachments.uploadedBy],
      references: [users.id],
    }),
  })
);

export const commentsRelations = relations(comments, ({ one }) => ({
  finding: one(findings, {
    fields: [comments.findingId],
    references: [findings.id],
  }),
  author: one(users, {
    fields: [comments.authorId],
    references: [users.id],
  }),
}));

export const reportTemplatesRelations = relations(
  reportTemplates,
  ({ one, many }) => ({
    organization: one(organizations, {
      fields: [reportTemplates.organizationId],
      references: [organizations.id],
    }),
    createdBy: one(users, {
      fields: [reportTemplates.createdBy],
      references: [users.id],
    }),
    exports: many(reportExports),
  })
);

export const reportExportsRelations = relations(reportExports, ({ one }) => ({
  project: one(projects, {
    fields: [reportExports.projectId],
    references: [projects.id],
  }),
  template: one(reportTemplates, {
    fields: [reportExports.templateId],
    references: [reportTemplates.id],
  }),
  exportedBy: one(users, {
    fields: [reportExports.exportedBy],
    references: [users.id],
  }),
}));

export const activityLogsRelations = relations(activityLogs, ({ one }) => ({
  actor: one(users, {
    fields: [activityLogs.actorId],
    references: [users.id],
  }),
}));

export const rolesRelations = relations(roles, ({ one, many }) => ({
  organization: one(organizations, {
    fields: [roles.organizationId],
    references: [organizations.id],
  }),
  permissions: many(rolePermissions),
  userAssignments: many(userRoles),
  invitations: many(userInvitations),
}));

export const rolePermissionsRelations = relations(rolePermissions, ({ one }) => ({
  role: one(roles, {
    fields: [rolePermissions.roleId],
    references: [roles.id],
  }),
}));

export const userRolesRelations = relations(userRoles, ({ one }) => ({
  user: one(users, {
    fields: [userRoles.userId],
    references: [users.id],
  }),
  role: one(roles, {
    fields: [userRoles.roleId],
    references: [roles.id],
  }),
  assignedBy: one(users, {
    fields: [userRoles.assignedBy],
    references: [users.id],
  }),
}));

export const userInvitationsRelations = relations(userInvitations, ({ one }) => ({
  organization: one(organizations, {
    fields: [userInvitations.organizationId],
    references: [organizations.id],
  }),
  role: one(roles, {
    fields: [userInvitations.roleId],
    references: [roles.id],
  }),
  invitedBy: one(users, {
    fields: [userInvitations.invitedBy],
    references: [users.id],
  }),
}));

// Insert schemas
export const insertOrganizationSchema = createInsertSchema(organizations).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertProjectSchema = createInsertSchema(projects, {
  startDate: z.coerce.date().optional(),
  endDate: z.coerce.date().optional(),
}).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertFindingSchema = createInsertSchema(findings).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertEvidenceSchema = createInsertSchema(evidenceAttachments).omit({
  id: true,
  createdAt: true,
});

export const insertCommentSchema = createInsertSchema(comments).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertTemplateSchema = createInsertSchema(reportTemplates).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertCredentialSchema = createInsertSchema(projectCredentials).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertPostmanCollectionSchema = createInsertSchema(postmanCollections).omit({
  id: true,
  createdAt: true,
});

export const insertReportExportSchema = createInsertSchema(reportExports).omit({
  id: true,
  createdAt: true,
});

export const insertRoleSchema = createInsertSchema(roles).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertRolePermissionSchema = createInsertSchema(rolePermissions).omit({
  id: true,
  createdAt: true,
});

export const insertUserRoleSchema = createInsertSchema(userRoles).omit({
  id: true,
  createdAt: true,
});

export const insertUserInvitationSchema = createInsertSchema(userInvitations).omit({
  id: true,
  createdAt: true,
});

// Types
export type UpsertUser = typeof users.$inferInsert;
export type User = typeof users.$inferSelect;
export type Organization = typeof organizations.$inferSelect;
export type Project = typeof projects.$inferSelect;
export type ProjectMember = typeof projectMembers.$inferSelect;
export type Finding = typeof findings.$inferSelect;
export type EvidenceAttachment = typeof evidenceAttachments.$inferSelect;
export type Comment = typeof comments.$inferSelect;
export type ReportTemplate = typeof reportTemplates.$inferSelect;
export type ReportExport = typeof reportExports.$inferSelect;
export type ActivityLog = typeof activityLogs.$inferSelect;
export type ProjectCredential = typeof projectCredentials.$inferSelect;
export type PostmanCollection = typeof postmanCollections.$inferSelect;
export type Role = typeof roles.$inferSelect;
export type RolePermission = typeof rolePermissions.$inferSelect;
export type UserRole = typeof userRoles.$inferSelect;
export type UserInvitation = typeof userInvitations.$inferSelect;

export type InsertOrganization = z.infer<typeof insertOrganizationSchema>;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type InsertProject = z.infer<typeof insertProjectSchema>;
export type InsertFinding = z.infer<typeof insertFindingSchema>;
export type InsertEvidence = z.infer<typeof insertEvidenceSchema>;
export type InsertComment = z.infer<typeof insertCommentSchema>;
export type InsertTemplate = z.infer<typeof insertTemplateSchema>;
export type InsertCredential = z.infer<typeof insertCredentialSchema>;
export type InsertPostmanCollection = z.infer<typeof insertPostmanCollectionSchema>;
export type InsertReportExport = z.infer<typeof insertReportExportSchema>;
export type InsertRole = z.infer<typeof insertRoleSchema>;
export type InsertRolePermission = z.infer<typeof insertRolePermissionSchema>;
export type InsertUserRole = z.infer<typeof insertUserRoleSchema>;
export type InsertUserInvitation = z.infer<typeof insertUserInvitationSchema>;

// Enum value types for frontend
export type ProjectStatus = (typeof projectStatusEnum.enumValues)[number];
export type FindingStatus = (typeof findingStatusEnum.enumValues)[number];
export type Severity = (typeof severityEnum.enumValues)[number];
export type CredentialType = (typeof credentialTypeEnum.enumValues)[number];
export type ReportTemplateType = (typeof reportTemplateTypeEnum.enumValues)[number];
export type UserRoleType = (typeof userRoleEnum.enumValues)[number];
export type Permission = (typeof permissionEnum.enumValues)[number];

