import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";

type FindingStatus = "draft" | "submitted" | "company_review" | "remediation_in_progress" | "ready_for_retest" | "verified_fixed" | "risk_accepted" | "closed";
