// Form validation schema
const createProjectSchema = z.object({
  name: z.string().min(1, "Project name is required"),
  startDate: z.string().min(1, "Start date is required"),
  endDate: z.string().min(1, "End date is required"),
  description: z.string().optional(),
  customerName: z.string().optional(),
  scope: z.string().optional(),
  methodology: z.string().optional(),
  tags: z.string().optional(),
  postmanCollectionType: z.enum(["none", "upload", "link"]).optional(),
  postmanLink: z.string().url().optional().or(z.literal("")),
});

type CreateProjectForm = z.infer<typeof createProjectSchema>;

function CreateProjectDialog() {
  const [isOpen, setIsOpen] = useState(false);
  const [postmanFile, setPostmanFile] = useState<File | null>(null);
  const { toast } = useToast();

  const form = useForm<CreateProjectForm>({
    resolver: zodResolver(createProjectSchema),
    defaultValues: {
      name: "",
      startDate: "",
      endDate: "",
      description: "",
      customerName: "",
      scope: "",
      methodology: "",
      tags: "",
      postmanCollectionType: "none",
      postmanLink: "",
    },
  });

  const createProjectMutation = useMutation({
    mutationFn: async (data: CreateProjectForm) => {
      const projectData = {
        name: data.name,
        description: data.description || null,
        customerName: data.customerName || "TBD",
        scope: data.scope || null,
        methodology: data.methodology || null,
        startDate: data.startDate,
        endDate: data.endDate,
        tags: data.tags ? data.tags.split(",").map(tag => tag.trim()).filter(Boolean) : [],
      };

      return apiRequest("POST", "/api/projects", projectData);
    },
    onSuccess: () => {
      toast({
        title: "Project created",
        description: "The project has been created successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/projects"] });
      setIsOpen(false);
      form.reset();
      setPostmanFile(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create project. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: CreateProjectForm) => {
    // Validate end date is after start date
    if (new Date(data.endDate) <= new Date(data.startDate)) {
      form.setError("endDate", {
        message: "End date must be after start date",
      });
      return;
    }

    createProjectMutation.mutate(data);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.type !== "application/json") {
        toast({
          title: "Invalid file type",
          description: "Please select a JSON file for Postman collection.",
          variant: "destructive",
        });
        return;
      }
      setPostmanFile(file);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button data-testid="button-new-project">
          <Plus className="h-4 w-4 mr-2" />
          New Project
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Create New Project</DialogTitle>
          <DialogDescription>
            Create a new penetration testing project. Fields marked with * are required.
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            {/* Mandatory Fields */}
            <div className="space-y-4">
              <h3 className="text-sm font-medium text-muted-foreground">Required Information</h3>

              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Project Name *</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="e.g., Healthcare Portal Security Assessment"
                        {...field}
                        data-testid="input-project-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="startDate"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Start Date *</FormLabel>
                      <FormControl>
                        <Input
                          type="date"
                          {...field}
                          data-testid="input-start-date"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="endDate"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>End Date *</FormLabel>
                      <FormControl>
                        <Input
                          type="date"
                          {...field}
                          data-testid="input-end-date"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </div>

            {/* Optional Fields */}
            <div className="space-y-4">
              <h3 className="text-sm font-medium text-muted-foreground">Additional Information</h3>

              <FormField
                control={form.control}
                name="customerName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Customer Name</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="e.g., HealthTech Solutions"
                        {...field}
                        data-testid="input-customer-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Project Description</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Describe the scope and objectives of this security assessment..."
                        rows={3}
                        {...field}
                        data-testid="textarea-description"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="scope"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Scope</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Define what is included in the assessment scope..."
                        rows={2}
                        {...field}
                        data-testid="textarea-scope"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="methodology"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Methodology</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="e.g., OWASP Testing Guide, NIST SP 800-115..."
                        rows={2}
                        {...field}
                        data-testid="textarea-methodology"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="tags"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Tags</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="web, api, mobile, compliance (comma-separated)"
                        {...field}
                        data-testid="input-tags"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            {/* Postman Collection */}
            <div className="space-y-4">
              <h3 className="text-sm font-medium text-muted-foreground">Postman Collection</h3>

              <FormField
                control={form.control}
                name="postmanCollectionType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Postman Collection</FormLabel>
                    <FormControl>
                      <Select value={field.value} onValueChange={field.onChange}>
                        <SelectTrigger data-testid="select-postman-type">
                          <SelectValue placeholder="Select option" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="none">No Postman Collection</SelectItem>
                          <SelectItem value="upload">Upload Collection File</SelectItem>
                          <SelectItem value="link">Postman Link</SelectItem>
                        </SelectContent>
                      </Select>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {form.watch("postmanCollectionType") === "upload" && (
                <div className="space-y-2">
                  <label className="text-sm font-medium">Collection File</label>
                  <div className="flex items-center gap-2">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => document.getElementById("postman-file")?.click()}
                      data-testid="button-upload-postman"
                    >
                      <Upload className="h-4 w-4 mr-2" />
                      Choose File
                    </Button>
                    {postmanFile && (
                      <span className="text-sm text-muted-foreground">{postmanFile.name}</span>
                    )}
                  </div>
                  <input
                    id="postman-file"
                    type="file"
                    accept=".json"
                    onChange={handleFileChange}
                    className="hidden"
                    data-testid="input-postman-file"
                  />
                </div>
              )}

              {form.watch("postmanCollectionType") === "link" && (
                <FormField
                  control={form.control}
                  name="postmanLink"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Postman Collection Link</FormLabel>
                      <FormControl>
                        <div className="flex items-center gap-2">
                          <LinkIcon className="h-4 w-4 text-muted-foreground" />
                          <Input
                            placeholder="https://www.postman.com/collections/..."
                            {...field}
                            data-testid="input-postman-link"
                          />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              )}
            </div>

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsOpen(false)}
                className="flex-1"
                data-testid="button-cancel-project"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createProjectMutation.isPending}
                className="flex-1"
                data-testid="button-create-project"
              >
                {createProjectMutation.isPending ? "Creating..." : "Create Project"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

function ProjectTile({ project }: { project: ProjectWithStats }) {
  const isOverdue = project.status !== 'complete' && project.endDate && new Date() > project.endDate;
  const endDate = project.endDate;

  return (
    <Card className="hover-elevate transition-all duration-200">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="space-y-1 flex-1">
            <CardTitle className="text-lg leading-6">{project.name}</CardTitle>
            <CardDescription className="text-sm">{project.customerName}</CardDescription>
          </div>
          <StatusBadge status={project.status} />
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Project Info */}
        <div className="grid grid-cols-2 gap-3 text-sm">
          <div className="flex items-center gap-2">
            <Calendar className="h-4 w-4 text-muted-foreground" />
            <span className="text-muted-foreground">Start:</span>
            <span>{project.startDate ? format(project.startDate, "MMM d, yyyy") : 'TBD'}</span>
          </div>
          <div className="flex items-center gap-2">
            <Clock className={`h-4 w-4 ${isOverdue ? 'text-red-500' : 'text-muted-foreground'}`} />
            <span className="text-muted-foreground">Due:</span>
            <span className={isOverdue ? 'text-red-500 font-medium' : ''}>
              {endDate ? format(endDate, "MMM d, yyyy") : 'TBD'}
            </span>
          </div>
          <div className="flex items-center gap-2">
            <Users className="h-4 w-4 text-muted-foreground" />
            <span className="text-muted-foreground">Team:</span>
            <span>{project.teamSize} members</span>
          </div>
          <div className="flex items-center gap-2">
            <FileText className="h-4 w-4 text-muted-foreground" />
            <span className="text-muted-foreground">Findings:</span>
            <span>{project.findingStats?.total || 0}</span>
          </div>
        </div>

        {/* Progress Bar (for in-progress projects) */}
        {project.status === 'in_progress' && project.progress && (
          <div className="space-y-1">
            <div className="flex justify-between text-xs text-muted-foreground">
              <span>Progress</span>
              <span>{project.progress}%</span>
            </div>
            <div className="w-full bg-secondary rounded-full h-2">
              <div 
                className="bg-primary h-2 rounded-full transition-all duration-300" 
                style={{ width: `${project.progress}%` }}
              />
            </div>
          </div>
        )}

        {/* Finding Stats */}
        {(project.findingStats?.total || 0) > 0 && (
          <div className="flex gap-1 flex-wrap">
            {(project.findingStats?.critical || 0) > 0 && (
              <Badge variant="destructive" className="text-xs px-2 py-0">
                {project.findingStats?.critical || 0} Critical
              </Badge>
            )}
            {(project.findingStats?.high || 0) > 0 && (
              <Badge variant="secondary" className="text-xs px-2 py-0 bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
                {project.findingStats?.high || 0} High
              </Badge>
            )}
            {(project.findingStats?.medium || 0) > 0 && (
              <Badge variant="secondary" className="text-xs px-2 py-0 bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
                {project.findingStats?.medium || 0} Medium
              </Badge>
            )}
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex gap-2 pt-2">
          <Link href={`/projects/${project.id}`} className="flex-1">
            <Button variant="default" size="sm" className="w-full" data-testid={`button-view-project-${project.id}`}>
              View Details
            </Button>
          </Link>
          {project.status === 'in_progress' && (
            <Button 
              variant="outline" 
              size="sm"
              data-testid={`button-submit-report-${project.id}`}
              onClick={() => {
                // TODO: Navigate to report submission
                console.log('Submit report for project:', project.id);
              }}
            >
              Submit Report
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

export default function Projects() {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("all");

  // Real API call with fallback to mock data
  const { data: projects, isLoading, error } = useQuery<ProjectWithStats[]>({
    queryKey: ['/api/projects'],
    queryFn: async () => {
      const response = await fetch('/api/projects', {
        credentials: 'include'
      });
      if (!response.ok) {
        // If authentication fails, fall back to empty array (will use mock data)
        if (response.status === 401) {
          return [];
        }
        throw new Error('Failed to fetch projects');
      }
      const data = await response.json();
      // Transform backend projects to include stats (for now with default values)
      return data.map((project: Project) => ({
        ...project,
        teamSize: 1, // Default team size
        findingStats: { 
          total: 0, 
          critical: 0, 
          high: 0, 
          medium: 0, 
          low: 0, 
          informational: 0 
        },
        progress: 0 // Default progress
      }));
    },
    retry: false, // Don't retry on auth failures
    initialData: [], // Provide empty array as initial data to prevent undefined access
  });

  // Use mock data if API fails or returns empty results
  const projectsData = (projects && projects.length > 0) ? projects : mockProjects;

  const filteredProjects = projectsData.filter((project: ProjectWithStats) => {
    const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         project.customerName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || project.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-muted-foreground">Loading projects...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold" data-testid="heading-projects">Projects</h1>
          <p className="text-muted-foreground">
            Manage penetration testing projects and security assessments
          </p>
        </div>
        <CreateProjectDialog />
      </div>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search projects..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
            data-testid="input-search-projects"
          />
        </div>
        <Select value={statusFilter} onValueChange={setStatusFilter}>
          <SelectTrigger className="w-full sm:w-48" data-testid="select-status-filter">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="planned">Planned</SelectItem>
            <SelectItem value="in_progress">In Progress</SelectItem>
            <SelectItem value="complete">Complete</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Project Stats */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold">{projectsData.filter((p: ProjectWithStats) => p.status === 'in_progress').length}</div>
            <div className="text-sm text-muted-foreground">Active Projects</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold">{projectsData.filter((p: ProjectWithStats) => p.status === 'planned').length}</div>
            <div className="text-sm text-muted-foreground">Planned Projects</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold">
              {projectsData.reduce((sum: number, p: ProjectWithStats) => sum + (p.findingStats?.total || 0), 0)}
            </div>
            <div className="text-sm text-muted-foreground">Total Findings</div>
          </CardContent>
        </Card>
      </div>

      {/* Projects Grid */}
      {filteredProjects.length === 0 ? (
        <Card>
          <CardContent className="p-8 text-center">
            <FileText className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-medium mb-2">No projects found</h3>
            <p className="text-muted-foreground mb-4">
              {searchTerm || statusFilter !== "all" 
                ? "Try adjusting your search terms or filters"
                : "Get started by creating your first project"
              }
            </p>
            {!searchTerm && statusFilter === "all" && (
              <Button data-testid="button-create-first-project">
                <Plus className="h-4 w-4 mr-2" />
                Create First Project
              </Button>
            )}
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
          {filteredProjects.map((project: ProjectWithStats) => (
            <ProjectTile key={project.id} project={project} />
          ))}
        </div>
      )}
    </div>
  );
}

